// Note: n8n Header Auth credential automatically validates the authorization header
// This code node just extracts and processes the webhook data

// Extract incoming data from the webhook
const message = $input.first().json.body.message || $input.first().json.message;
const conversationHistory = $input.first().json.body.conversationHistory || [];
const sessionId = $input.first().json.body.sessionId || 'session-' + Date.now();

// Validate message
if (!message) {
  throw new Error('No message provided');
}

// Return structured data
return [{
  json: {
    message: message,
    conversationHistory: conversationHistory,
    sessionId: sessionId,
    timestamp: new Date().toISOString()
  }
}];

// Format the AI agent's response
const agentOutput = $input.first().json.output || $input.first().json.text || 'I apologize, but I encountered an error. Please try asking your question again.';

// Return formatted response
return [{
  json: {
    response: agentOutput,
    model: 'n8n AI Agent (Groq Llama 3)',
    timestamp: new Date().toISOString(),
    sessionId: $('Extract Request Data').first().json.sessionId
  }
}];
